################################################################
# program main loop
################################################################
	
main:
	jal init_backup
	# draw objects at their initial positions
	jal draw_score
	jal draw_bullet_cnt
	jal draw_platforms
	li $a0, COLOR_PICKUPS
	jal draw_pickups
	li $a0, COLOR_PLAYER
	jal draw_player
	li $s0, 1 # main loop's loop variable
	# enter main loop
main_loop:
	jal draw_platforms
	# update pickups if $s0 % PICKUP_MOVE_FREQ == 0
	remu $t0, $s0, PICKUP_MOVE_FREQ
	#andi $t0, $s0, PICKUP_MOVE_FREQ
	bnez $t0, main_loop_update_player
	# update pickups
	# 1. erase pickups at their previous positions
	li $a0, COLOR_BG
	jal draw_pickups
	# 2. calculate next positions for pickups
	jal upate_pickups
	# 3. draw pickups at next positions
	li $a0, COLOR_PICKUPS
	jal draw_pickups
	# update player
main_loop_update_player:
	# convert keyboard input into direction (save in v0)
	jal read_keyboard
	move $s1, $v0
	# update bullets
	li $a0, COLOR_BG
	jal draw_bullets
	jal update_bullets
	li $a0, COLOR_BULLET
	jal draw_bullets
	# erase player at previous position
	li $a0, COLOR_BG
	jal draw_player
	# calculate next player position
	move $a0, $s1
	jal move_player
	# draw player at next position
	li $a0, COLOR_PLAYER
	jal draw_player
	jal draw_score
	jal draw_bullet_cnt
	jal update_player_state
	jal check_score
	# wait for an interval
	li $v0, 32 # sleep system call
	li $a0, REFRESH_INTERVAl
	syscall
	# next loop
	addi $s0, $s0, 1
	j main_loop

################################################################
# functions related to draw objects
################################################################

# args: (row, col) -> return position
# $a0: row, $a1: col -> $a0: result
framebuffer_position:
	sll $a0, $a0, L_ROW_BYTES #row (2^8)
	sll $a1, $a1, 2 # col (4)
	add $a0, $a0, $a1
	li $a1, BASE_ADDRESS
	add $a0, $a0, $a1 #This line adds the base address of the frame buffer to the previously calculated pixel address, resulting in the absolute address of the desired pixel in memory
	jr $ra

# arg: a0 -> color for pickups
draw_pickups:
	move $t4, $a0 # t4: input color for pickups
	la $t0, pickups
	lb $t1, 0($t0) # t1: size of pickups array
	addi $t0, $t0, 1 # t0 now point to first pick up
	li $t2, 0 # loop variable
draw_pickups_loop:
	beq $t1, $t2, draw_pickups_ret
	move $t3, $t4
	lb $a0, 0($t0) # a0: row
	lb $a1, 1($t0) # a1: col
	lb $a2, 2($t0) # a2: score
	beq $t3, COLOR_BG, draw_1_pickup # if the pickup hasn't drawn then draw it
	bgtz $a2, draw_1_pickup
	bltz $a2, draw_neg_pickup
	# score is 0, which means this pickup has been removed
	li $t3, COLOR_BG
	j draw_1_pickup
draw_neg_pickup:
	# score less than 0, change color to COLOR_ENEMIES
	li $t3, COLOR_ENEMIES
draw_1_pickup:
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal framebuffer_position
	# now a0 is the position of framebuffer
	# restore ra
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	sw $t3, 0($a0) # draw 1 unit (first row)
	addi $a0, $a0, ROW_BYTES
	sw $t3, 0($a0) # second row
	sw $t3, -4($a0) # second row
	sw $t3, 4($a0) # second row
	addi $a0, $a0, ROW_BYTES
	sw $t3, 0($a0) # third row
	addi $t0, $t0, 3 # move to next pickup
	addi $t2, $t2, 1 # increase loop variable
	j draw_pickups_loop
draw_pickups_ret:
	jr $ra

draw_platforms:
	la $t0, platforms	# t0: address of platform array size
	lb $t1, 0($t0)   	# t1: array size
	addi $t0, $t0, 1	# t0: address of 1st platform
	li $t2, COLOR_PLATFORM # t2: color
	li $t3, 0		# t3: outer loop variable
# outer loop
draw_platforms_outer_loop:
	beq $t1, $t3, draw_platforms_outer_loop_end
	lb $a0, 0($t0) # a0: row
	lb $a1, 1($t0) # a1: col
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal framebuffer_position
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	lb $t4, 2($t0) # t4: platform length
	li $t5, 0      # t5: inner loop variable
# inner loop
draw_platforms_inner_loop:
	beq $t4, $t5, draw_platforms_inner_loop_end
	sw $t2, 0($a0)
	addi $a0, $a0, 4
	addi $t5, $t5, 1
	j draw_platforms_inner_loop
draw_platforms_inner_loop_end:
	addi $t0, $t0, 3 # next platform
	addi $t3, $t3, 1
	j draw_platforms_outer_loop
draw_platforms_outer_loop_end:
	jr $ra
	
# arg: a0 -> color for player
draw_player:
	move $t1, $a0
	la $t0, player
	lb $a0, 0($t0) # player row
	lb $a1, 1($t0) # player col
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal framebuffer_position
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	sw $t1, 0($a0) # first row of player
	li $a1, ROW_BYTES
	add $a0, $a0, $a1 # move to next row
	sw $t1, 0($a0) # second row of player
	add $a0, $a0, $a1 # move to next row
	sw $t1, 0($a0) # third row of player
	jr $ra

# Draw the first six scores to be red and last three scores to be grey(initial state for score)
draw_score:
	la $t0, score #score = 6
	lb $t1, 0($t0) # current score
	li $t0, 0 # loop variable
	li $t2, BASE_ADDRESS # framebuffer pointer
	addi $t2, $t2, ROW_BYTES
	li $t3, COLOR_RED
	li $t4, COLOR_BG
	li $t5, COLOR_GREY
draw_score_loop:
	beq $t0, $t1, draw_score_bg
	sw $t3, 0($t2)
	sw $t3, 4($t2)
	sw $t3, 8($t2)
	sw $t3, 12($t2)
	sw $t3, 16($t2)
	sw $t3, 20($t2)
	sw $t3, 24($t2)
	sw $t4, 28($t2)
	addi $t0, $t0, 1
	addi $t2, $t2, 32
	j draw_score_loop
draw_score_bg:
	beq $t0, MAX_SCORE, draw_score_bg_end
	sw $t5, 0($t2)
	sw $t5, 4($t2)
	sw $t5, 8($t2)
	sw $t5, 12($t2)
	sw $t5, 16($t2)
	sw $t5, 20($t2)
	sw $t5, 24($t2)
	sw $t4, 28($t2)
	addi $t0, $t0, 1
	addi $t2, $t2, 32
	j draw_score_bg
draw_score_bg_end:
	jr $ra


################################################################
# functions related to collision check
################################################################

# if a given (row, col) is on any platform, return true else false
# a0: row, a1: col -> return v0: true/false
is_on_platform:
	la $t0, platforms
	lb $t1, 0($t0) # number of platforms
	addi $t0, $t0, 1 # t0 point to 1st array element
	li $t2, 0 # loop variable
is_on_platform_loop:
	beq $t1, $t2, is_on_platform_loop_end
	lb $t3, 0($t0) # paltform row
	# check if row == player_row
	bne $t3, $a0, is_on_platform_loop_skip
	# check if col in range [platform_col, platform_col + platform_length)
	lb $t3, 1($t0) # platform_col
	bltu $a1, $t3, is_on_platform_loop_skip # 1. check if player_col < platform_col
	lb $t4, 2($t0) # platform_length
	add $t3, $t3, $t4
	bgeu $a1, $t3, is_on_platform_loop_skip # 2. check if player_col >= platform_col + platform_length
	li $v0, 1
	jr $ra  # return True (1)
is_on_platform_loop_skip:
	addi $t0, $t0, 3 # next platform
	addi $t2, $t2, 1 # next loop
	j is_on_platform_loop
is_on_platform_loop_end:
	li $v0, 0
	jr $ra # return False (0)


# check if the player is on any paltforms
# a0: player_row, a1: player_col -> return v0
player_is_on_platform:
	# check first row of player
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal is_on_platform
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	beq $v0, 1, player_is_on_platform_ret_true
	# check scond row
	addi $a0, $a0, 1
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal is_on_platform
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	beq $v0, 1, player_is_on_platform_ret_true
	# last row
	addi $a0, $a0, 1
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal is_on_platform
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	beq $v0, 1, player_is_on_platform_ret_true
	li $v0, 0
	jr $ra # return flase
player_is_on_platform_ret_true:
	li $v0, 1
	jr $ra # return true
	
# check if the player is on any pickups
# a0: player_row, a1: player_col, a2: is_player -> return v0
# if a2 is 1, (is palyer), score will change
# is a2 is 0, (is bullet), score won't change
check_pickups_collision:
	sll $t5, $a0,L_WIDTH
	add $t5, $t5, $a1 # idx of player
	la $t0, pickups
	lb $t1, 0($t0) # number of pickups
	addi $t0, $t0, 1 # t0: 1st pickup
	li $t2, 0 # loop variable
	li $v0, 0 # result
check_pickups_loop:
	beq $t1, $t2, check_pickups_end
	lb $t3, 0($t0) # pickup row
	lb $t4, 1($t0) # pickup col
	lb $t6, 2($t0) # pickup score
	beqz $t6, check_pickups_loop_continue # already removed
	bnez $a2, check_pickup
	# bullets should ignore pickups with positive score
	bgtz $t6, check_pickups_loop_continue
check_pickup:
	# first unit of pickup
	sll $t3, $t3, L_WIDTH
	add $t3, $t3, $t4 # idx of pickup
	beq $t3, $t5, pickup_hit
	# second row
	addi $t3, $t3, WIDTH
	beq $t3, $t5, pickup_hit
	addi $t3, $t3, -1
	beq $t3, $t5, pickup_hit
	addi $t3, $t3, 2
	beq $t3, $t5, pickup_hit
	addi $t3, $t3, -1
	# third row
	addi $t3, $t3, WIDTH
	bne $t3, $t5, check_pickups_loop_continue
pickup_hit:
	beqz $a2, delete_pickup
	# pickup hit
	# update score
	la $t6, score
	lb $t7, 0($t6) # total score
	lb $t4, 2($t0) # pickup's score
	add $t7, $t7, $t4
	sb $t7, 0($t6) # update total score
delete_pickup:
	sb $zero, 2($t0) # delete pickup
	addi $v0, $v0, 1
check_pickups_loop_continue:
	addi $t2, $t2, 1
	addi $t0, $t0, 3
	j check_pickups_loop
check_pickups_end:
	jr $ra


################################################################
# functions related to update object postitions
################################################################

upate_pickups:
	la $t0, pickups
	lb $t1, 0($t0) # t1: size of pickups array
	addi $t0, $t0, 1 # t0 now point to first pick up
	li $t2, 0 # loop variable
update_pickups_loop:
	beq $t1, $t2, update_pickups_ret
	lb $t3, 1($t0) # t3: col
	lb $t4, 2($t0) # t4: score
	beqz $t4, next_pickup
	beq $t3, MAX_COL, pickup_reach_max_col
	addi $t3, $t3, 1
	j next_pickup
pickup_reach_max_col:
	li $t3, 0
next_pickup:
	sb $t3, 1($t0) # updat col
	addi $t0, $t0, 3 # move to next pickup
	addi $t2, $t2, 1 # increase loop variable
	j update_pickups_loop
update_pickups_ret:
	jr $ra

# return v0: {UP, DOWN, LEFT, RIGHT} or 0
read_keyboard:
	li $t0, 0xffff0000
	lw $t1, 0($t0)
	bne $t1, 1, read_keyboard_default
	lw $t1, 4($t0)
	beq $t1, 0x77, respond_to_w
	beq $t1, 0x61, respond_to_a
	beq $t1, 0x73, respond_to_s
	beq $t1, 0x64, respond_to_d
	beq $t1, 0x6a, respond_to_j
	beq $t1, 0x70, restart_game
read_keyboard_ret:
	jr $ra
respond_to_w:
	la $t2, player
	lb $t3, 2($t2) # player state
	# if the palyer is still in S_JMP_2, discard this jump
	beq $t3, S_JMP_2, read_keyboard_ret
	li $v0, UP
	beq $t3, S_JMP_1, set_s_jmp_2
	li $t3, S_JMP_1 # normal -> jmp1
	sb $t3, 2($t2)
	j read_keyboard_ret
set_s_jmp_2:
	li $t3, S_JMP_2 # jmp1 -> jmp2
	sb $t3, 2($t2)
	j read_keyboard_ret
respond_to_a:
	li $v0, LEFT
	j read_keyboard_ret
respond_to_s:
	li $v0, DOWN
	j read_keyboard_ret
respond_to_d:
	li $v0, RIGHT
	j read_keyboard_ret
respond_to_j:
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal fire
	lw $ra, 0($sp)
	addi $sp, $sp, 4
read_keyboard_default:
	li $v0, 0
	j read_keyboard_ret

# a0: move direction
move_player:
	# t0: how many steps to move, UP: JMP_DIST, {LEFT, RIGHT, 0}: 1, DOWN: 2
	beq $a0, UP, move_up
	beq $a0, DOWN, move_down
	li $t0, 1
	li $t1, DOWN
	movz $a0, $t1, $a0 # if a0 is 0, set a0 to DOWN (due to gravity)
	j move_loop_prepare
move_up:
	li $t0, JMP_DIST
	j move_loop_prepare
move_down:
	li $t0, 2 # 1 + gravity
move_loop_prepare:
	li $t1, 0 # loop variable
	la $t2, player
	lb $a1, 0($t2) # player_row
	lb $a2, 1($t2) # player_col
move_loop:
	beq $t0, $t1, move_loop_end
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	# (a0, a1, a2) -> (v0, v1): next player position
	jal move_player_1_step
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	# we want to call player_is_on_platform
	# push stack
	sw $t0, -4($sp) # loop cnt
	sw $t1, -8($sp) # loop variable
	sw $a0, -12($sp) # move direction
	sw $a1, -16($sp) # current_row
	sw $a2, -20($sp) # current_col
	sw $v0, -24($sp) # next_row
	sw $v1, -28($sp) # next_col
	sw $ra, -32($sp) # ra
	addi $sp, $sp, -32
	# prepare args for player_is_on_platform
	move $a0, $v0
	move $a1, $v1
	# check if (next_row, next_col) is a valid position
	jal player_is_on_platform
	move $t2, $v0 # move result to t2
	# pop stack
	lw $ra, 0($sp)
	lw $v1, 4($sp)
	lw $v0, 8($sp)
	lw $a2, 12($sp)
	lw $a1, 16($sp)
	lw $a0, 20($sp)
	lw $t1, 24($sp)
	lw $t0, 28($sp)
	addi $sp, $sp, 32
	# if next_position is valid, update (a1, a2)
	bnez $t2, move_loop_end # else break loop
	move $a1, $v0
	move $a2, $v1
	addi $t1, $t1, 1
	# check pickups
	# push stack
	sw $t0, -4($sp) # loop cnt
	sw $t1, -8($sp) # loop variable
	sw $a0, -12($sp) # move direction
	sw $a1, -16($sp) # current_row
	sw $a2, -20($sp) # current_col
	sw $ra, -24($sp) # ra
	addi $sp, $sp, -24
	# prepare args for check_pickups_collision
	move $a0, $a1
	move $a1, $a2
	li $a2, 1 # 1 indicate this is a player
	jal check_pickups_collision
	lw $ra, 0($sp)
	lw $a2, 4($sp)
	lw $a1, 8($sp)
	lw $a0, 12($sp)
	lw $t1, 16($sp)
	lw $t0, 20($sp)
	addi $sp, $sp, 24
	j move_loop
move_loop_end:
	# update memory
	la $t2, player
	sb $a1, 0($t2)
	sb $a2, 1($t2)
	jr $ra
	
# arg: a0, a1, a2 -> return (v0, v1)
# a0: move direction
# (a1, a2): player_row, player_col
# v0: player_row after move 1 step
# v1: palyer_col after move 1 step
move_player_1_step:
	move $v0, $a1 # row
	move $v1, $a2 # col
	beq $a0, UP, move_player_up
	beq $a0, DOWN, move_player_down
	beq $a0, LEFT, move_player_left
	beq $a0, RIGHT, move_player_right
move_player_ret:
	jr $ra
move_player_up:
	beq $v0, MIN_ROW, move_player_ret
	addi $v0, $v0, -1
	j move_player_ret
move_player_down:
	beq $v0, MAX_ROW, move_player_ret
	addi $v0, $v0, 1
	j move_player_ret
move_player_left:
	beqz $v1, move_player_ret
	addi $v1, $v1, -1
	j move_player_ret
move_player_right:
	beq $v1, MAX_COL, move_player_ret
	addi $v1, $v1, 1
	j move_player_ret


################################################################
# additional features
################################################################

update_player_state:
	la $t0, player
	lb $a0, 0($t0) # row
	lb $a1, 1($t0) # col
	addi $a0, $a0, 3 # bottom of player
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal is_on_platform
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	bnez $v0, set_s_normal
	# still in mid air
	jr $ra
set_s_normal:
	la $t0, player
	li $t1, S_NORMAL
	sb $t1, 2($t0)
	jr $ra
	

init_backup:
	la $t0, platforms # t0: read ptr
	la $t1, backup # t1: write ptr
	move $t2, $t1 # end address
init_backup_loop:
	beq $t0, $t2, init_backup_end
	lb $t3, 0($t0)
	sb $t3, 0($t1)
	addi $t0, $t0, 1
	addi $t1, $t1, 1
	j init_backup_loop
init_backup_end:
	jr $ra

flush_screen:
	li $t0, 0
	li $t1, FRAMEBUFFER_SIZE
	li $t2, BASE_ADDRESS
	li $t3, COLOR_BG
flush_screen_loop:
	beq $t0, $t1, flush_end
	sw $t3, 0($t2)
	addi $t2, $t2, 4
	addi $t0, $t0, 1
	j flush_screen_loop
flush_end:
	jr $ra

restart_game:
	jal flush_screen
	la $t0, platforms
	la $t1, backup
	move $t2, $t1
restart_loop:
	beq $t0, $t2, restart_end
	lb $t3, 0($t1)
	sb $t3, 0($t0)
	addi $t0, $t0, 1
	addi $t1, $t1, 1
	j restart_loop
restart_end:
	j main


# arg: a0: start address of memory
display:
	li $t0, 0
	li $t1, 4096
	move $t2, $a0
	li $t3, BASE_ADDRESS
display_loop:
	beq $t0, $t1, display_end
	lw $t4, 0($t2)
	sw $t4, 0($t3)
	addi $t0, $t0, 1
	addi $t2, $t2, 4
	addi $t3, $t3, 4
	j display_loop
display_end:
	jr $ra

wait_for_restart:
	li $t0, 0xffff0000
	lw $t1, 0($t0)
	bne $t1, 1, wait_sleep
	lw $t1, 4($t0)
	beq $t1, 0x70, restart_game
wait_sleep:
	li $v0, 32 # sleep system call
	li $a0, REFRESH_INTERVAl
	syscall
	j wait_for_restart

check_score:
	la $a0, score
	lb $a1, 0($a0)
	beqz $a1, score_is_zero
	beq $a1, MAX_SCORE, score_reach_max
	jr $ra
score_is_zero:
	la $a0, gameover
	jal display
	jal draw_score
	j wait_for_restart
score_reach_max:
	la $a0, win
	jal display
	jal draw_score
	j wait_for_restart 
	
	
draw_bullet_cnt:
	la $t0, remain_bullets
	lb $t1, 0($t0) # remain bulltes
	li $t0, 0 # loop variable
	li $t2, BASE_ADDRESS # framebuffer pointer
	addi $t2, $t2, ROW_BYTES
	addi $t2, $t2, ROW_BYTES
	addi $t2, $t2, ROW_BYTES # point to 4th row
	li $t3, COLOR_BULLET
	li $t4, COLOR_BG
	li $t5, COLOR_GREY
draw_bullet_cnt_loop_1:
	beq $t0, $t1, draw_bullet_cnt_loop_2
	sw $t3, 0($t2)
	sw $t4, 4($t2)
	addi $t0, $t0, 1
	addi $t2, $t2, 8
	j draw_bullet_cnt_loop_1
draw_bullet_cnt_loop_2:
	beq $t0, MAX_BULLETS, draw_bullet_cnt_end
	sw $t5, 0($t2)
	sw $t4,4($t2)
	addi $t0, $t0, 1
	addi $t2, $t2, 8
	j draw_bullet_cnt_loop_2
draw_bullet_cnt_end:
	jr $ra


# arg: a0: color
draw_bullets:
	li $t0, MAX_BULLETS
	li $t1, 0 # loop variable
	la $t2, bullets
	move $t5, $a0 # color
draw_bullets_loop:
	beq $t0, $t1, draw_bullets_loop_end
	lb $t3, 0($t2) # row
	beq $t5, COLOR_BG, draw_1_bullet
	beqz $t3, draw_bullets_loop_continue
draw_1_bullet:
	lb $t4, 1($t2) # col
	move $a0, $t3
	move $a1, $t4
	addi $sp, $sp, -4
	sw $ra, 0($sp)
	jal framebuffer_position
	lw $ra, 0($sp)
	addi $sp, $sp, 4
	sw $t5, 0($a0)
draw_bullets_loop_continue:
	addi $t1, $t1, 1
	addi $t2, $t2, 2
	j draw_bullets_loop
draw_bullets_loop_end:
	jr $ra
	
fire:
	la $t0, remain_bullets
	lb $t1, 0($t0)
	beqz $t1, fire_end # no enough bullets
	li $t0, MAX_BULLETS
	li $t1, 0 # loop variable
	la $t2, bullets
fire_search_bullet:
	beq $t0, $t1, fire_end
	lb $t3, 0($t2) # row
	bnez $t3, fire_search_continue
	# unused bullet found
	la $t4, player
	lb $t3, 0($t4) # set bullet's row to player's row
	addi $t3, $t3, -1 # move up 1 unit
	lb $t4, 1($t4) # set bullet's col to player's col
	sb $t3, 0($t2)
	sb $t4, 1($t2)
	# remain_bullets -= 1
	la $t5, remain_bullets
	lb $t6, 0($t5)
	addi $t6, $t6, -1
	sb $t6, 0($t5)
	jr $ra
fire_search_continue:
	addi $t1, $t1, 1
	addi $t2, $t2, 2
	j fire_search_bullet
fire_end:
	jr $ra
	

update_bullets:
	li $t0, MAX_BULLETS
	li $t1, 0 # loop variable
	la $t2, bullets
update_bullets_loop:
	beq $t0, $t1, update_bullets_end
	lb $t3, 0($t2) # row
	beqz $t3, update_bullets_loop_continue # not a valid bullet
	addi $t3, $t3, -1 # move upward
	bleu $t3, MIN_ROW, reset_bullet # bullet is at top of the screen
	lb $t4, 1($t2) # col
	# now (t3, t4) is the next position of this bullet
	# if hit on a platform, reset the bullet
	sw $t0, -4($sp)
	sw $t1, -8($sp)
	sw $t2, -12($sp)
	sw $t3, -16($sp)
	sw $t4, -20($sp)
	sw $ra, -24($sp)
	addi $sp, $sp, -24
	move $a0, $t3
	move $a1, $t4
	jal is_on_platform
	lw $ra, 0($sp)
	lw $t4, 4($sp)
	lw $t3, 8($sp)
	lw $t2, 12($sp)
	lw $t1, 16($sp)
	lw $t0, 20($sp)
	addi $sp, $sp, 24
	bnez $v0, reset_bullet
col_bullet_pickup:
	# if hit a pickup, remove the pickup
	sw $t0, -4($sp)
	sw $t1, -8($sp)
	sw $t2, -12($sp)
	sw $t3, -16($sp)
	sw $t4, -20($sp)
	sw $ra, -24($sp)
	addi $sp, $sp, -24
	move $a0, $t3
	move $a1, $t4
	li $a2,0 # 0 indicate this is a bullet
	jal check_pickups_collision
	lw $ra, 0($sp)
	lw $t4, 4($sp)
	lw $t3, 8($sp)
	lw $t2, 12($sp)
	lw $t1, 16($sp)
	lw $t0, 20($sp)
	addi $sp, $sp, 24
	bnez $v0, reset_bullet
	# not on any platform, not hit any pickups, move upward
	sb $t3, 0($t2)
	sb $t4, 1($t2)
	j update_bullets_loop_continue
reset_bullet:
	sb $zero, 0($t2)
	sb $zero, 1($t2)
update_bullets_loop_continue:
	addi $t2, $t2, 2 # next bullet
	addi $t1, $t1, 1
	j update_bullets_loop
update_bullets_end:
	jr $ra
	
	
	
	

